import React, { useState } from 'react';
import {Finder} from './Finder';
import Contacts from './Contacts';
//import './Header.css';

export function Header(){

    const headerColors = {
        lifeGreen: 'green',
        bigBlue: '#25358b',
        smallGrey : 'rgb(32, 29, 29)'
    }

    const [color, setColor] = useState(headerColors.bigBlue); 
    const [changSetColor, setChangSetColor] = useState(headerColors.lifeGreen);

    let buttonColorName;

    function ChangeColor(){
         if(color === headerColors.bigBlue){
            setColor(changSetColor);
            setChangSetColor(color);
            buttonColorName = 'Green';            
         }else{
            setColor(changSetColor);
            setChangSetColor(color);
            buttonColorName = 'Blue';
         }

    }



       return(
        <div className= 'header'style={{backgroundColor:color}} >
        <Contacts />
        <Finder />
        <button onClick= {ChangeColor} >
            {buttonColorName}
        </button>
        </div>
    );
}


